def parse_csv_line(line):
    fields = []
    current_field = ""
    in_quotes = False
    for char in line:
        if char == '"':
            in_quotes = not in_quotes
        elif char == ',' and not in_quotes:
            fields.append(current_field)
            current_field = ""
        else:
            if in_quotes or char != '"': # Only add if inside quotes or not a quote
                current_field += char
    fields.append(current_field)
    return fields
