from flask import Flask, jsonify


app = Flask(__name__)


@app.route('/greet/<username>')
def greet(username):
    greeting = {"message": f"Hello, {username}! Welcome!"}
    return jsonify(greeting)


if __name__ == '__main__':
    app.run(debug=True)

